mapper.update:
UPDATE f_scene
SET sname = #{sname},update_date=NOW()
WHERE
scene_id = #{sceneId}
AND mid = (
SELECT
id
FROM
f_mesh
WHERE
mesh_id = #{meshId}
AND uid = #{uid}
)

mapper.delete:
DELETE
FROM
f_scene
WHERE
scene_id = #{sceneId}
AND mid = (
SELECT
id
FROM
f_mesh
WHERE mesh_id = #{meshId}
AND uid = #{uid}
)
