mapper.update:
UPDATE f_scene
SET sname = #{sname},update_date=NOW()
WHERE
scene_id = #{sceneId}
AND mid = (
SELECT
id
FROM
f_mesh
WHERE
mesh_id = #{meshId}
AND uid = #{uid}
)

mapper.select:
SELECT COUNT(0) total
FROM f_light l
INNER JOIN f_mesh m ON m.id = l.mid
WHERE 1 = 1
AND m.mesh_id = #{meshId}
AND m.uid = #{uid}

SELECT m.mesh_id, s.scene_id, s.sname FROM f_mesh m INNER JOIN f_scene s ON m.id = s.mid WHERE s.uid = #{uid} AND m.mesh_id = #{meshId}
AND s.scene_id=#{sceneId}
GROUP BY m.mesh_id,s.scene_id,s.sname

mapper.delete:
DELETE
FROM
f_scene
WHERE
scene_id = #{sceneId}
AND mid = (
SELECT
id
FROM
f_mesh
WHERE mesh_id = #{meshId}
AND uid = #{uid}
)
