mapper.update:
UPDATE f_group
SET gname=#{gname},update_date=now()
WHERE mid=#{mid} and group_id=#{groupId}

UPDATE f_group
SET gname=#{gname},update_date=now()
WHERE mid=(select id from f_mesh where mesh_id=#{meshId}) and group_id=#{groupId}

update f_light
set pid=#{pid}
where mid=(select id from f_mesh where mesh_id=#{meshId}) and gid=(select id from f_group where mid=(select id from f_mesh where mesh_id=#{meshId}) and group_id=#{groupId})
